import { Component } from 'react'

class App extends Component {
  constructor(){
    super();
    this.state = {
      name: ""
    }
  }

  render(){
    return(
      <div>
        <h2>Enter you name:</h2>
        <input type="text"
               value={this.state.name}
               placeholder="your name"
               onChange={ (e) => this.setState({...this.state, name: e.target.value})} />
        <h1>Hello, {this.state.name}</h1>       
      </div>
    );
  }

}

export default App;
